function initSocialSharing(){const e=document.getElementById("projectModal");if(e){e.querySelectorAll(".share-btn").forEach((t=>{t.addEventListener("click",(function(n){n.preventDefault();const o=e.querySelector("h3")?e.querySelector("h3").textContent:"Marco Ruch Project",c=e.querySelector(".project-description")?e.querySelector(".project-description").textContent:"Check out this project by Marco Ruch, C# and Angular Developer",r=window.location.href;t.classList.contains("share-linkedin")?window.open(`https://www.linkedin.com/sharing/share-offsite/?url=${encodeURIComponent(r)}`,"_blank"):t.classList.contains("share-twitter")?window.open(`https://twitter.com/intent/tweet?url=${encodeURIComponent(r)}&text=${encodeURIComponent(o+" - "+c)}`,"_blank"):t.classList.contains("share-facebook")?window.open(`https://www.facebook.com/sharer/sharer.php?u=${encodeURIComponent(r)}`,"_blank"):t.classList.contains("share-email")&&(window.location.href=`mailto:?subject=${encodeURIComponent(o)}&body=${encodeURIComponent(c+"\n\n"+r)}`)}))}))}document.querySelectorAll(".contact-info .share-buttons .share-btn").forEach((e=>{}))}document.addEventListener("DOMContentLoaded",(function(){initSocialSharing()}));